			<script type="text/javascript">
				//定义数据
				var type = {};
				type['no'] = "请选择";
				type['goldetf'] = "黄金ETF";
				type['silveretf'] = "白银ETF";
				//search js begin
				var SearchEtf = {
					Init:function(type){
						this.type = type;
						this.selType = $$("selType");
						this.btn = $$("btnSearch");
						
						var calendar1 = new Calendar("startdate", false, false, [140, 800, 0.95, "solid", "#EEE", "#FFF", 3, 3, 0.2, "#000", "#444", "#F6F8FD", "#333", "#F1F5FC", "#000", "#EFF3FB", false, false]);
						var calendar2 = new Calendar("enddate", false, false, [140, 800, 0.95, "solid", "#EEE", "#FFF", 3, 3, 0.2, "#000", "#444", "#F6F8FD", "#333", "#F1F5FC", "#000", "#EFF3FB", false, false]);
						$$("startdate").value = '2018-10-05';
						$$("enddate").value = '2019-10-05';

						fillByArr(this.selType,window['type'],this.type);
						this.btn.onclick = function(){
							var pattern = /^(\d{4})(-)(\d{2})(-)(\d{2})$/; 
							this.selType = $$("selType");
							var startDate = $$("startdate").value,
							endDate = $$("enddate").value;
							var index = this.selType.value;
							if (index == "no") {
								alert("请选择ETF品种!");
								return ;
							}
							
							if (startDate != '' && startDate.match(pattern) == null)
							{
								alert("请输入正确的开始日期格式 YYYY-MM-DD");
								return;
							}
							if (endDate != '' && endDate.match(pattern) == null)
							{
								alert("请输入正确的结束日期格式 YYYY-MM-DD");
								return;
							}
							if (startDate != '' && endDate !='' && !compareDate(endDate, startDate))
							{
								alert("结束日期应该大于开始日期");
								return;
							}
							window.location = "?code="+index+"&startdate="+startDate+"&enddate="+endDate;
						};
					}
				};

				//比较日期大小
				function compareDate(DateOne,DateTwo)
				{
					var OneMonth = DateOne.substring(5,DateOne.lastIndexOf ("-"));
					var OneDay = DateOne.substring(DateOne.length,DateOne.lastIndexOf ("-")+1);
					var OneYear = DateOne.substring(0,DateOne.indexOf ("-"));

					var TwoMonth = DateTwo.substring(5,DateTwo.lastIndexOf ("-"));
					var TwoDay = DateTwo.substring(DateTwo.length,DateTwo.lastIndexOf ("-")+1);
					var TwoYear = DateTwo.substring(0,DateTwo.indexOf ("-"));

					if (Date.parse(OneMonth+"/"+OneDay+"/"+OneYear) > Date.parse(TwoMonth+"/"+TwoDay+"/"+TwoYear))
					{
						return true;
					}
					else
					{
						return false;
					} 
				};
				function fillByArr(obj, items, m){
					obj.innerHTML = "";
					var n=0;
					for (var i in items) {
						obj.options.add(new Option(items[i],i));
						if (m != 'undefined' && i == m)
						{
							obj.options[n].selected = true;
						}
						n++;
					};
				};
			</script>
