                _head += '</th></tr>';
                $('#data_thead').html(_head);
                
                //body part
                _body += '<tr><td><div class="tbodyTitle">日期</div></td>' +
                        '<td><div class="tbodyTitle">净持仓量（盎司）</div></td>'+
                        '<td><div class="tbodyTitle">净持仓量（吨）</div></td>'+
                        '<td><div class="tbodyTitle">总价值（美元）</div></td>'+
                        '<td><div class="tbodyTitle">持仓量增减（盎司）</div></td></tr>';
                for(var i in _data){
                    var m = _data[i];
                    //console.log(m);
                    _body += '<tr>';
                    _body += '<td><div class="td">'+ m.date +'</div></td>';
                    _body += '<td><div class="td">' + m.netounces + '</div></td>';
                    _body += '<td><div class="td">' + m.nettonnes + '</div></td>';
                    _body += '<td><div class="td">' + m.totalvalues + '</div></td>';
                    _body += '<td><div class="td">' + m.ouncecahnge + '</div></td>';
                    _body += '</tr>';
                };
                $('#data_tbody').html(_body);
            });
            return false;
        }
        
